import React from 'react';
import './Showquotes.css';

class Showquotes extends React.Component {

  //function to show what todo when button is clicked 
  displayQuotes= (props) =>{
    const allProps = this.props.quotes
    //go through props
    //randomly pick one
    let selectedQuote = allProps[Math.floor(Math.random() * allProps.length)]
    //display what is picked 
    console.log(selectedQuote)
    return selectedQuote
  }


  render() {
    const randomQuote = this.displayQuotes()
    const text = randomQuote.text
    const author = randomQuote.author
    return (
    <div className='container'>
      <p> Click button to see new  quotes</p>
      <div className='quotes'>
        <h1>quote: {text} </h1>
        <p> author: {author}</p>
        <button className='mybutton' onClick={this.displayQuotes}> New quote</button>
      </div>
    </div>
    )
  }
}

export default Showquotes;
