import React from 'react';
import './Showquotes.css';

class Showquotes extends React.Component {
  constructor(props){
    super(props)
    this.state ={
      text: '',
      author:''
    }
  }

  //function to show what todo when button is clicked 
  displayQuotes= (props) =>{
    const allProps = this.props.quotes
    //go through props
    //randomly pick one
    let selectedQuote = Math.floor(Math.random() * allProps.length)
    //display what is picked 
    return selectedQuote
  }


  render() {
    const {text, author} = this.displayQuotes()
    return (
    <div className='container'>
      <p> Click button to see new  quotes</p>
      <div className='quotes'>
        <h1>quote: </h1>
        <p> author:</p>
        <button className='mybutton' onClick={this.displayQuotes}> New quote</button>
      </div>
    </div>
    )
  }
}

export default Showquotes;
